<form [formGroup]="loginFormulario">
  <div >
    <label> Email </label>
    <input type="email" class="form-control"  placeholder="Enter email" formControlName="email">
    <div *ngIf="email.invalid && (email.dirty || email.touched)" class="text-danger" >
      <div *ngIf="email.errors?.['required']"> 
        El email es obligatorio</div>
      <div *ngIf="email.errors?.['email']">
        El email debe tener un formato valido
      </div>

</div>
  </div>

  <div>
    <label> Contraseña</label>
    <input type="password" class="form-control" placeholder="Contraseña" formControlName="pass">
  </div>
  <div>
  <button type="submit" (click)="login()" class="btn btn-primary">Iniciar Sesión</button>
  </div>
  <div>
    <input type="text" name="texto" id="">
  </div>
</form>
