    <ul class="navbar-nav ">
      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="home" >Inicio</a>
      </li>

      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="search">Buscar</a>
      </li>    

      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="auth/loginForm" >Iniciar Sesión</a>
      </li>

      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="auth/signupForm" >Registrarse</a>
      </li>  

      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="" (click)= "logOut()">
          Log out
        </a>       
      </li>   

