import { Component, Input } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-tarjetas',
  // standalone: true,
  // imports: [CommonModule],
  templateUrl: './tarjetas.component.html',
  styleUrl: './tarjetas.component.css'
})
export class TarjetasComponent {

  @Input() items:any [] = [];

    constructor(private router:Router){ }
    
    verArtista(item:any){
      let artistaId;

      //el type es para diferenciar si es artista o es album
      if (item.type==='artist') {
        artistaId = item.id
      } else {
        artistaId = item.artists[0].id
      }
      //reedireccion a la pagina del artista
      this.router.navigate([`/artist`, artistaId])
    }


}
<div  class="card-columns mt-3 animate__animated animate__fadeIn">

  <div (click)="verArtista(item)" class="card puntero" *ngFor="let item of items">
    <img [src]="item.images | noimage" class="card-img-top border-primary" alt="...">
    <div class="card-body">
      <h5 class="card-title text-center">{{item.name}}</h5>
      <p class="card-text" style="justify-content: center;">
          <!-- <span class="badge badge-pill badge-primary text-center" *ngFor="let artist of item.artists">{{artist.name}}</span> -->
          <span class="badge badge-light text-center" *ngFor="let artist of item.artists">{{artist.name}}</span>
          <span class="badge badge-light text-center"> 
            Nº Tracks: <span clas="badge badge-light text-center"> {{item.total_tracks}} </span>
          </span>
      </p>
    </div>
  </div>

</div>
