import { Component, OnDestroy, OnInit } from '@angular/core';
// import { LoginService } from 'src/app/services/auth/login/login.service';
// import { SignService } from 'src/app/services/auth/sign/sign.service';
// import { signupForm } from 'src/app/auth/form-signup/'
import {AuthService, Credential} from '../../../services/auth/firebase/auth.service'
// import { AuthService } from 'src/app/services/auth/firebase/auth.service';
import { Subscription } from 'rxjs';



@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  user: any;
  authSubscription!: Subscription;
  // user: Credential.email;
  // const {email , password} = this.credencial.email

  constructor(
    // private serviciodeLogin:LoginService, 
    // private serviciodeSign: SignService,
    private authSrv: AuthService,
  ) { }

  ngOnInit(): void {
    // this.serviciodeLogin.currentUserLoginOn.subscribe(
    //   {
    //     next:(userLoginOn) => {
    //       this.userLoginOn=userLoginOn;
    //     }
    //   }
    // )

    //this.serviciodeSign.
    this.authSubscription = this.authSrv.authState$.subscribe(user => {
      this.user = user;
    });
  }

  ngOnDestroy(): void {
    if (this.authSubscription) {
      this.authSubscription.unsubscribe();
    }
  }
  

  logOut(): void {
    this.authSrv.logOut();
  }
}
<!--<nav class="navbar navbar-expand-sm navbar-dark bg-dark"> -->
  <nav class="navbar navbar-expand-sm navbar-dark bg-dark ">
  
    <a class="navbar-brand" href="/nohome"> <img src="assets/img/logo.png" width="30" height="30" alt="" loading="lazy"> FiestApp </a>

  <!--BOTON VISTA MOVIL-->
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
  </button>
  
  <!--BOTONS DEL NAVBAR-->
  <!--<div class="collapse navbar-collapse" id="navbarSupportedContent"></div>-->
  <div class="collapse navbar-collapse"  id="navbarSupportedContent"  style="vertical-align:middle;">
    <ul class="navbar-nav ">
      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="home" >Inicio</a>
      </li>

      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="search">Buscar</a>
      </li>    

      <!-- <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="auth/app-log-in">Iniciar Sesión</a>
      </li>

      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="auth/app-sign-up">Registrarse</a>
      </li>   -->
      <ng-container *ngIf="!userLoginOn; else loggedInTemplate">
        <li class="nav-item" routerLinkActive="active">
          <a class="nav-link" routerLink="auth/app-log-in">Iniciar Sesión</a>
        </li>

        <li class="nav-item" routerLinkActive="active">
          <a class="nav-link" routerLink="auth/app-sign-up">Registrarse</a>
        </li>
      </ng-container>
     
      <ng-template #loggedInTemplate>
        <li class="nav-item">
          <button class="btn btn-outline-primary" id="dropdownBasic1" ngbDropdownToggle>
            <img style="border-radius: 100%;" *ngIf="user?.photoURL" [src]="user?.photoURL" width="30" height="30" class="d-inline-block align-top" alt="">
            {{ user?.email }}
          </button>
        </li>
      </ng-template>

      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="" (click)= "logOut()">
        Cerrar Sesión
        </a>       
      </li>   

      <!-- NAVBAR - SERVICIO OK -->
      <!-- <li *ngIf="!userLoginOn" class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="/login">Iniciar Sesión</a>
      </li>

      <li *ngIf="userLoginOn" class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="/nohome">Cerrar Sesion</a>
      </li>  

      <li *ngIf="!userLoginOn" class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="/signup"> Registrarse</a>
      </li> -->
     



    </ul>
  
  </div>
  </nav>
