import { Component, OnDestroy, OnInit } from '@angular/core';
import { User } from 'firebase/auth';
import { Subscription } from 'rxjs';
import { AuthService } from 'src/app/services/auth/firebase/auth.service'; // Asegúrate de ajustar la ruta según la ubicación de tu servicio de autenticación

@Component({
  selector: 'app-perfil',
  templateUrl: './perfil.component.html',
  styleUrls: ['./perfil.component.css']
})
export class PerfilComponent implements OnInit, OnDestroy {

  user: User | null = null;
  private userSubscription: Subscription | undefined;

  constructor(private authService: AuthService) { }

  ngOnInit(): void {
    this.userSubscription = this.authService.getUserLogged().subscribe(user => {
      this.user = user;
    });
  }

  ngOnDestroy(): void {
    if (this.userSubscription) {
      this.userSubscription.unsubscribe();
    }
  }

}
<div class="container">
  <ng-template #loggedInTemplate>
    <li class="nav-item">
      <a class="nav-link" routerLink="/perfil">
        <img
          style="border-radius: 100%"
          *ngIf="user?.photoURL"
          [src]="user?.photoURL"
          alt=""
        />
        {{ user?.email }}
      </a>
    </li>
  </ng-template>
</div>
