import { Component} from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { SpotifyService } from 'src/app/services/spotify.service';

@Component({
  selector: 'app-artista',
  templateUrl: './artista.component.html',
  styleUrls: ['./artista.component.css']
})


export class ArtistaComponent{

  artista:any={};

  //topTracks almacenara todo el array
  topTracks:any[] = [];
  loadingArtista:boolean;

  constructor(private spotify:SpotifyService, private router:ActivatedRoute) {

    this.loadingArtista=true;

    this.router.params.subscribe(params =>{
      //console.log(params)
      //la pasamos el parametro ID
      this.getInfoArtista(params[`id`]);

      //incializamos con el nombre del metodo que creamos en spotify.service
      this.getTopTracks(params[`id`]);
    })
  }

  getInfoArtista(id:string){

    this.loadingArtista=true;

    this.spotify.getInfoArtista(id).subscribe(res=>{
      console.log(res);

      this.artista=res;

      //para cuando se haya cargado la info, dejara de salir la carga
      this.loadingArtista=false;
    })
  }

  //NUEVO METODO, info musica
  getTopTracks(id:string) {
    this.spotify.getTopTracks(id).subscribe(res=>{
      console.log(res);
      this.topTracks = res;
    })
  }



}
<app-loading *ngIf="loadingArtista"> </app-loading>


<div class="caja_cabeza row animate__animated animate__fadeIn" *ngIf="!loadingArtista" >
    <div class="col-4">
    <!--<div class="col-2"> -->
        <img [src]="artista.images | noimage" class="img-fluid img-thumbnail " >
    </div>
    <!--<div class="col">-->
    <div class="col-8">
        <h3>{{artista.name}}</h3>
        <p><strong>Popularidad:</strong><span class="badge badge-pill badge-secondary">{{artista.popularity}}</span></p>
        <p><strong>Seguidores:</strong><span class="badge badge-pill badge-success">{{artista.followers.total}}</span></p>
        <p><strong>Géneros:</strong><span class="badge badge-pill badge-primary" *ngFor="let genero of artista.genres">{{genero}}</span></p>
        <p> <a [href]="artista.external_urls.spotify" class="puntero" target="_blank">Ir a la pagina del artista</a> </p>
        <button routerLink="/search" class="btn btn-outline-warning">Regresar</button>
    </div>

</div>    

    <div class="row text-center mt-5">
        <div class="col">
            <!-- <p>hola</p> -->
            <table class="table colorFontTable">
                <thead>
                    <tr class="tr_head">
                        <!-- <th></th> -->
                        <th>Album</th>
                        <th>Canción</th>
                        <th>Vista Previa</th>
                    </tr>
                </thead>   

                <tbody>
                    <!-- track es un nombre cualquiera y topTracks existe en .ts -->
                    <tr *ngFor="let track of topTracks" class="tr_body">
                        <td class="img_album"> <img [src]="track.album.images | noimage" class="img-tab"> {{track.album.name}} </td>
                        <!-- <td style="vertical-align:middle;"> {{track.album.name}}</td> -->
                        <td class="song_name" style="vertical-align:middle;">{{track.name}}</td>
                        <td style="vertical-align:middle;">
                            <!--la url de la preview no es segura-->
                            <!--track.uri es el enlace de la cancion, asi es como llega en el array-->
                            <iframe [src]="track.uri | linkseguro" width="400" height="100" [frameBorder]="0"  allow="encrypted-media"></iframe>

                        </td>
                    </tr>
                </tbody> 
            </table>
        </div>
    </div>
