import { Component, OnDestroy, OnInit } from '@angular/core';
import { User } from 'firebase/auth';
import { Subscription } from 'rxjs';
import { CommonModule, NgIf } from '@angular/common';
import { AuthService } from 'src/app/services/auth/firebase/auth.service'; // Asegúrate de ajustar la ruta según la ubicación de tu servicio de autenticación

@Component({
  selector: 'app-perfil',
  standalone: true,
  imports: [
    CommonModule
  ],
  templateUrl: './perfil.component.html',
  styleUrls: ['./perfil.component.css']
})
export class PerfilComponent implements OnInit, OnDestroy {


  user: any;
  private authSubscription: Subscription | undefined;

  constructor(
    private authSrv: AuthService
  ) { }

  ngOnInit(): void {
    this.authSubscription = this.authSrv.authState$.subscribe(user => {
      this.user = user;
    });
  }

  ngOnDestroy(): void {
    if (this.authSubscription) {
      this.authSubscription.unsubscribe();
    }
  }

}
<!-- perfil.component.html -->
<div class="profile-container" *ngIf="user">
  <div class="profile-header">
    <img *ngIf="user.photoURL" [src]="user.photoURL" alt="Foto de perfil" class="profile-picture">
    <h2>{{ user.displayName || 'Nombre no disponible' }}</h2>
  </div>
  
  <div class="profile-details">
    <p><strong>Email:</strong> {{ user.email }}</p>
    <p *ngIf="user.providerData.length > 0"><strong>Proveedor de Autenticación:</strong> {{ user.providerData[0].providerId }}</p>
    <!-- Agrega más detalles según sea necesario -->
  </div>
</div>

<div class="profile-not-authenticated" *ngIf="!user">
  <p>Usuario no autenticado</p>
</div>
